import { Component, OnInit } from '@angular/core';
import { AlertController } from '@ionic/angular';
import { Router } from '@angular/router';
import { BienvenidaPage } from '../bienvenida/bienvenida.page';
import { Injectable } from '@angular/core';

interface componente {
  icon: string;
  name: string;
  redirectTo: string;
}

@Component,Injectable({
  selector: 'app-inicio',
  templateUrl: './inicio.page.html',
  styleUrls: ['./inicio.page.scss'],
    providedIn: 'root'
})
export class InicioPage implements OnInit {
  componentes: componente[] = [];


  constructor(
    private alertController: AlertController,
    private router: Router
  ) {}

  ngOnInit() {}

  async presentAlert() {
    const alert = await this.alertController.create({
      header: 'Bienvenido ingresa tu nombre',
      buttons: ['OK'],
      inputs: [
        {
          placeholder: 'Nombre',
          name: 'username'
        },
      ],
    });

    await alert.present();

    const { data } = await alert.onDidDismiss();

    if (data?.values?.username) {
      // Pasar el nombre de usuario a la p치gina BienvenidaPage utilizando el objeto state del enrutador
      this.router.navigateByUrl('/bienvenida', { state: { name: data.values.username } });
    } else {
      const errorAlert = await this.alertController.create({
        header: 'Error',
        message: 'Nombre de usuario no v치lido',
        buttons: ['OK'],
      });
      await errorAlert.present();
    }
  }
}
