import { Component, OnInit } from '@angular/core';
import { AlertController } from '@ionic/angular';
import { Router } from '@angular/router';

interface Componente {
  icon: string;
  name: string;
  redirectTo: string;
}

@Component({
  selector: 'app-inicio',
  templateUrl: 'inicio.page.html',
  styleUrls: ['inicio.page.scss'],
})
export class InicioPage implements OnInit {

  componentes: Componente[] = [];

  constructor(
    private alertController: AlertController,
    private router: Router
  ) {}

  async presentAlert() {
    const alert = await this.alertController.create({
      header: 'Bienvenido ingresa tu nombre',
      buttons: ['OK'],
      inputs: [
        {
          placeholder: 'Nombre',
          name: 'username'
        },
      ],
    });


  salir() {
    (window.navigator as any).app.exitApp();
  }

  ngOnInit() {
    const loggedIn = sessionStorage.getItem('loggedIn');
    console.log('loggedIn:', loggedIn);
    if (loggedIn && loggedIn === 'true') {
      console.log('Sesión válida');
      // Si la sesión es válida, redirigimos al usuario a la página de bienvenida
      this.router.navigateByUrl('/bienvenida');
    }
  }

}
<div style="margin: auto; text-align: center;">
  <h1>C-PumP Exercises</h1>
  <ion-button margin (click)="presentAlert()" style="margin: auto;">Iniciar Sesión </ion-button>
</div>
<header> <ion-button margin (click)="salir()"class="salir-button" style="margin: auto;">Salir </ion-button></header>
